import React, { Component } from "react";
//import Search from "../Search"
//import "./style.css";
//import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
//import { faSort, faSortUp, faSortDown } from '@fortawesome/free-solid-svg-icons'
import homes from '../../homes.json'
import HomeCard from '../layout/houseCard'
import Filters from '../search/filterBar'
import './style.css'

class HouseList extends Component {
  state = {
    homeData: homes,
    homeList: homes,
    searchCity: [],
    searchBed: [],
    searchPrice: []
  };

  state = {
    empData: [],
    empList: [],
    filterList: [],
    currentSort: 'default',
    searchInput: ""
  };



  onSortChange = () => {
    const { currentSort } = this.state;
    let nextSort;

    if (currentSort === 'down') nextSort = 'up';
    else if (currentSort === 'up') nextSort = 'default';
    else if (currentSort === 'default') nextSort = 'down';

    this.setState({
      currentSort: nextSort
    });
  };


  handleInputChange = event => {
    this.setState({ searchInput: event.target.value });

    let filterList = this.state.empData.filter(person => {
      //console.log(person.name.first)
      return person.name.first.includes(event.target.value)
        || person.name.last.includes(event.target.value)
        || person.email.includes(event.target.value)
        || person.phone.includes(event.target.value)
        || person.dob.date.includes(event.target.value)
    });
    const empList = filterList;
    this.setState({ empList });
  }



  handleInputChange = event => {
    this.setState({ searchInput: event.target.value });

    let city = this.state.homes.filter(home => {
      //console.log(person.name.first)
      return home.address.city.includes(event.target.value)
    });
    const homeList = city;
    this.setState({ homeList });
  }

  render() {
    /*const { currentSort } = this.state;
    const sortTypes = {
      up: {
        class: 'sort-up',
        fn: (a, b) => a.name.first > (b.name.first) ? 1 : -1
      },
      down: {
        class: 'sort-down',
        fn: (a, b) => a.name.first < (b.name.first) ? 1 : -1
      },
      default: {
        class: 'Sort',
        fn: (a, b) => a
      }
    };*/

    return (

      <div className="main">

        <Filters value={this.state.searchCity} handleInputChange={this.handleInputChange} />
        {this.state.homeList.map(home => (
          <HomeCard
            image={home.property.primaryImageUrl}
            id={home.id}
            bed={home.property.numberBedrooms}
            bath={home.property.numberBaths}
            sq={home.property.squareFeet}
            address={home.property.address.addressLine1} 
            city={home.property.address.city}
            state={home.property.address.state}
            zip={home.property.address.zip}
          />
        ))}

      </div>

    );
  }


}


export default HouseList;
