import React, { Component } from "react";
//import Search from "../Search"
//import "./style.css";
//import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
//import { faSort, faSortUp, faSortDown } from '@fortawesome/free-solid-svg-icons'
import homes from '../../homes.json'
import HomeCard from '../layout/houseCard'
import Filters from '../search/filter'
import './style.css'

class HouseList extends Component {
  state = {
    homeData: homes,
    homeList: homes,
    searchCity: [],
    searchBed: [],
    searchPrice: []
  };

  handleBeds = event => {
    this.setState({ searchInput: event.target.value });

    let city = this.state.homeData.filter(home => {
      //console.log(person.name.first)
      return home.property.numberBedrooms === event.target.value
    });
    const homeList = city;
    this.setState({ homeList });
  }

  handleInputChange = event => {
    this.setState({ searchBed: event.target.value });

    let city = this.state.homeData.filter(home => {
      //console.log(person.name.first)
      return home.property.address.city.includes(event.target.value)
    });
    const homeList = city;
    this.setState({ homeList });
  }

  render() {
    /*const { currentSort } = this.state;
    const sortTypes = {
      up: {
        class: 'sort-up',
        fn: (a, b) => a.name.first > (b.name.first) ? 1 : -1
      },
      down: {
        class: 'sort-down',
        fn: (a, b) => a.name.first < (b.name.first) ? 1 : -1
      },
      default: {
        class: 'Sort',
        fn: (a, b) => a
      }
    };*/

    return (

      <div className="main">

        <Filters 
            value={this.state.searchInput} handleInputChange={this.handleInputChange} 
            value={this.state.searchBed} handleBeds={this.handleBeds} />
        {this.state.homeList.map(home => (
          <HomeCard
            image={home.property.primaryImageUrl}
            id={home.id}
            bed={home.property.numberBedrooms}
            bath={home.property.numberBaths}
            sq={home.property.squareFeet}
            address={home.property.address.addressLine1} 
            city={home.property.address.city}
            state={home.property.address.state}
            zip={home.property.address.zip}
          />
        ))}

      </div>

    );
  }


}


export default HouseList;
